//Ethan Alexander Shulman 2018
mat2 rot2d(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c,s,-s,c);
}
void mainImage(out vec4 o, in vec2 u)
{
	vec2 uv = u/iResolution.y,iv;
    float ls = 1., n = 0.;
    for (float s = 2.; s < 16.; s += s) {
        if (s > 2.+floor(mod(iTime,24.))) break;
        uv *= s;
        iv = floor(uv/ls);
        ls = s;
        if (mod(iv.x+iv.y,2.) < 1.) {
            uv = abs((fract(uv)-.5)*rot2d(s+iv.x+iv.y*9.874))*2.;
            n++;
        }
    }
    o = cos(texture(iChannel0,uv*.04)*6.28+sin((iTime+n)*.07*vec4(1,2,7,4))*4.)*0.5+0.5;
}
