//کاری از سید مرتضی کمالی

#define step(a,b) smoothstep( a -2./iResolution.y , a +2./iResolution.y , b)
#define circle( uvcoord,movement,maskcoord,radius,maskradius) vec3(clamp(step(length(uvcoord+movement),radius)*1.0-step(length(uvcoord+movement+maskcoord),maskradius),0.0,1.0))
#define Diamond(uv,radius) vec3(step( uv.x, uv.y-radius)+step( uv.x, -uv.y-radius))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.y;
    uv *= 1.9;
    vec2 UvSign = uv;
 UvSign += vec2(0.16,0.05);
  //Big Crescents
  vec3 BigRight = circle(UvSign,vec2(0.1,0.0),vec2(0.17,-0.1),0.8,0.8);
  vec3 BigLeft = circle(UvSign,vec2(-0.1,0.0),vec2(-0.17,-0.1),0.8,0.8);

  //Small Crescents by symmetry

  UvSign.x = -abs(UvSign.x);    
  vec3 SmallCrescents = circle(UvSign,vec2(0.35,-0.1),vec2(-0.28,0.1),0.65,0.78);


  	//Middle
    UvSign.y += 0.05;
  vec3 Middle = 1.0-vec3(clamp(Diamond(UvSign,0.85 ) + step( UvSign.x, -0.1),0.0,1.0));

    //UpMiddle
    UvSign.x +=0.05;  
    vec3 UpMiddle = circle(UvSign,vec2(0.0,-0.98),vec2(0.0,-0.09),0.10,0.10);


   vec3 Up = vec3(step(0.99,uv.y)) * (1.0-vec3(0.0,1.0,0.0));
   vec3 Down = vec3(step(uv.y,-0.99))* (1.0-vec3(1.0,0.0,0.0));
    vec3 Flag = Up+Down;


    vec3 AllahSign = (SmallCrescents + Middle + BigRight+BigLeft +  UpMiddle) * (1.0-vec3(1.0,0.0,0.0));
    AllahSign = 1.0 - AllahSign;
  fragColor = vec4(AllahSign - Flag,1.0);
}
