// golfed version of https://www.shadertoy.com/view/4l3XD2

/**/  // -17 by coyote   -2 by Fab

#define C(P,b) smoothstep(-b,b, .06 - length( max( abs(P+.5-fract(U)*R/R.y) - vec2(3,2)*.08, 0.)) )

void mainImage(out vec4 O, vec2 u) {
    vec2  R = iResolution.xy,
          U = u/R*3.;
    float t = iTime,
          y = ceil(U.y = -U.y),
          a = .06 + .05* sin( t*5. + U.x*3. - y*1.3 );
       U.x -= t * (.4-.3*y);
    O  -= C(a*.3, a*.5) * (.4 - a*1.6);
    O.b = .2 + .5*O.r;
    y   = C(-a, 3./R.y);
    O  += .4 - O*y; O.r += .3*y; }

/**/



/**   // 319 chars

#define C(P,b) smoothstep(-b,b, .06 - length( max(R-R, abs(P+.5-fract(U)*R/R.y) - vec2(3,2)*.08)) )

#define mainImage( O, u )                              \
    vec2  R = iResolution.xy,                          \
          U = u/R*3.;                                  \
    float t = iTime,                                   \
          y = ceil(U.y = -U.y),                        \
          a = .06 + .05* sin( t*5. + U.x*3. - y*1.3 ); \
       U.x -= t * (.4-.3*y);                           \
    vec4 B=O,F=O; O.b+=.2; B.rg-=.4; F.r+=.3;          \
    O = .4+ mix( mix( O,                               \
                      B, C(a*.3, a/2.) * (1.-a*4.) ),  \
                      F, C(-a, 3./R.y) )

/**/
