// goo?

void mainImage(out vec4 k, vec2 p)
{
    const vec4 col1 = vec4(0.17,.1,.1,1.0);
    const vec4 col2 = vec4(0.9,0.5,0.5,1.0);
    vec2 uv = p.xy / iResolution.xy;

    float speed = iTime*0.2;
 	float _fd = 1.0+uv.x*0.55;
    vec2 cst = vec2( cos(_fd+speed), sin(_fd+speed) );
    float zoom = 2.5+(0.5*sin(iTime*0.75));
    mat2 rot = zoom * mat2(cst.x,-cst.y,cst.y,cst.x);
    uv = rot*uv;


    float s = sin(iTime*0.1);
    float s2 = 0.5+sin(iTime*1.8);
    vec2 d = uv*(1.75+s*.3);

    d.x += sin(d.y + iTime*0.2)*1.2;
    d.y += sin(d.x + iTime*0.2)*0.5;	//-(s*0.5);
    float v1=length(0.5-fract(d.xy))+0.80;

    d = uv;	//*(4.0+s*2.5);


    d.x-=iTime*0.2;
    d.y-=iTime*0.1;
    float offset = mod( floor(d.y), 2.0);
    d.x += offset*0.5;

    float v2=length(0.5-fract(d.xy));
    v1 *= 1.0-v2*v1;
    v1 = v1*v1*v1;
    v1 *= 1.9+s2*0.2+offset*1.1;
    k = mix(col2,col1,v1)*(4.2+(s2*0.2));
    k *= 1.0-(v2);

    k = clamp(k,vec4(0.0),vec4(1.0));
    k = vec4(1.0) - k;


	float grid = mod( floor(8.0*uv.x) + floor(8.0*uv.y), 2.0);

    if (k.r<=0.8)
    {

        float m = 1.0-clamp(k.r,0.0,0.5);
        vec4 _col = (grid+0.5)*vec4(0.3,0.4,0.8,1.0);
        k=_col*m;
    }
    else
    {
        k = mix(col1,col2,k.r);
        k.g += offset*0.3;
        vec4 _col = 0.2*grid*vec4(0.375);
        k+=_col;

    }


}
