float Circle( vec2 uv, vec2 p, float r, float blur )
{

	float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);
    return c;

}

float Hash( float h )
{

    return h = fract(cos(h) * 5422.2465);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float c = 0.0;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    float sizer = 1.0;
    float steper = .1;
    for(float i = -sizer; i<sizer; i+=steper)
        for(float j = -sizer; j<sizer; j+=steper){
    {	
        float timer = .5;
        float resetTimer = 7.0;
    	//c = Circle(uv, vec2(0.0,0), sin(iTime*timer), 0.1);
    	//c -= Circle(uv, vec2(0.0,0.0), sin(iTime*timer+.2), 0.1);
        if(c<=1.0){

            c += Circle(uv, vec2(i, j),sin(Hash(i))*cos(Hash(j))*(mod(iTime*timer, resetTimer)), sin(Hash(j)));

        }
        else if(c>=1.0)
        {
            c -= Circle(uv, vec2(i, j),sin(Hash(i))*cos(Hash(j))*(mod(iTime*timer, resetTimer)), sin(Hash(j)));     
        }
        }
    }
    fragColor = vec4( vec3(c),1.0);
}
