void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime + 13.1;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 10.0*atan(t);
    uv = fract(uv+iTime);
    vec2 uv2 = fract(uv + t *0.23);
	vec4 P = vec4(uv2,0.5+0.5*cos(t),1.0);
    vec4 O = vec4(uv,0.5+0.5*cos(t),1.0);

    float M = step( (O.r / P.r), abs(sin(t * uv.y)));
    fragColor.rgb = vec3(M);
;

}
