void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float amt = 10.0;
    float threshold = 0.01;
    float gridX = iResolution.x / amt;
    float gridY = iResolution.y / amt;

    float gridX2 = iResolution.x / amt * 2.0;
    float gridY2 = iResolution.y / amt * 2.0;
    float splits = 10.0;

    float rmod = (mod(uv.x * gridX, splits) + threshold) / splits;
    float gmod = (mod(uv.y * gridY, splits) + threshold) / splits;

    float rmod2 = (mod(uv.x * gridX2, splits) + threshold) / splits;
    float gmod2 = (mod(uv.y * gridY2, splits) + threshold) / splits;

    float cgmod = ceil(gmod2 - gmod);
    float crmod = ceil(rmod2 - rmod);

    float icgmod = 1.0 - cgmod;
    float icrmod = 1.0 - crmod;

    float invmod = 1.0 - cgmod;

    float diamond = floor(rmod + gmod);
    float invdiamond = (1.0 - diamond);

    float diamond2 = floor(rmod2 + gmod2);
    float invdiamond2 = ceil(1.0 - diamond2);

    float mask = diamond2 * diamond + invdiamond * invdiamond2;
    float intersect = ceil(crmod * cgmod);

    float top = ceil(ceil(mask - intersect) - crmod) * cgmod;
    top = ceil(top / 10000.0);

    float iintersect = ceil((icgmod * icrmod)/ 1000.0);
    float intersect3 = ceil((icgmod * crmod) / 1000.0);
    float intersect4 = ceil((cgmod * crmod) / 1000.0);

    float shadowed = mask - top;

    float jump = iintersect + intersect3;
    float darkest = ceil((jump * diamond2 * iintersect + jump * invdiamond2 * intersect3) / 2.0);
    float medium = shadowed - darkest;


	fragColor = vec4(medium * 0.4 + darkest * 0.2 + top * 0.8, 0.0, 0.0, 1.0);
}
