void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float r = iResolution.y / 2.5;
    vec2 center = iResolution.xy / 2.0;
    float dis = distance(fragCoord.xy , center);
    float t = clamp(r - dis, 0.0, 1.0);


    float c = fragCoord.x / 8.0;

    c = cos(c - iTime) * 0.5 + 0.5;
    // c = step(0.5, c);

    vec4 background = vec4( 0.1, 0.2, 0.3, 1.0);
    vec4 color = vec4(c, c, c, min(t, c)); // min(t, c)

	fragColor = mix(background, color, color.a);
}
