// Entry for [2TC 15] (= 280 chars or less, as counted by Shadertoy)

#define T(X) texture (iChannel0, vec2 (X, 0)).x
void mainImage (out vec4 f, vec2 w) {
	float t = iTime, r = T (0) * .4, a = t;
	vec4 p = vec4 (w, 0, 1) / iResolution.y - .5, c = -p;
	for (float d = 1.; d > 0.; d -= .1)
		f = c += fract (d + t + vec4 (0, .7, .3, 0))
			* .07 * T (d)
			/ length (p.xy - r * vec2 (cos (a += .63), sin (a)));
}
