///compact version by FabriceNeyret2

#define s(k) .7*sin(i/32.*(k)+vec2(1.6,0))

void mainImage( out vec4 f, vec2 u ) {     
    for (float i=1.; i<=200.; i++) {
        vec2 a=s(1.), b=s(iTime/5.+2.)-a;
        a += 2.*u/iResolution.y -vec2(1.8,1);
        f += clamp( .23 - 33.*length( a + b*fract(-dot(a,b)/dot(b,b)) ), 0., .33);
	}
}
