const float numberOfSquares= 10.0;

float chessboard(vec2 uv)
{
    uv = floor(uv*numberOfSquares);
    return mod(uv.x+uv.y, 2.0);
}

void mainImage(out vec4 i, in vec2 c )
{
	vec2 p = c.xy / iResolution.y;

    float on = chessboard(p);

    vec3 onColor = vec3(1.0,0.0,0.0);
    vec3 offColor = vec3(0.0,1.0,0.0);

    i = vec4(vec3((on > 0.0 ) ? onColor : offColor ),1);
}
