/*
#define mainImage( O, I )        \
	vec2 p = I / iResolution.xy; \
	O += .9 + cos( 157.* (2.*p.x+p.y + .1* texture(iChannel0, p).g) )
*/


void mainImage( out vec4 O, in vec2 I )
{
	vec2 p = I.xy / iResolution.xy;
    float l = fract((2.*p.x+p.y+.1*texture(iChannel0, p).g)*25.);

	O = vec4(vec3( smoothstep(.1,.3,abs(l-.5)) ),1.0);
}
