vec2 rotate(vec2 p, float ang){
    float c = cos(ang), s = sin(ang);
    return vec2(p.y * c - p.x * s, p.y * s + p.x * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p =1.0*(fragCoord.xy / iResolution.xy)-0.5;


	p*=(20.0+80.0*(iMouse.x/iResolution.x));
	p*=sin(p.x)+cos(p.y);
	vec2 pp=vec2(p.x,p.y);
	pp=rotate(pp,iTime*2.0);
	vec3 cc=vec3(pp.x,pp.y,(pp.x+pp.y)/2.0);


    fragColor = vec4(cc,1.0);
}
