{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}

vec2 mainSound( in int samp,float time)
{
    time *= 6.0;
    float noise = sin(sin(cos(fract(time)*0.5+0.25)*80.0)*40.0) * (pow(1.0-fract(time),8.0))*2.0 * (pow(1.0-fract(time*0.33333)*0.5,3.0))*2.0;
    float noise4 = sin(sin(cos(fract(time+0.2)*0.5+0.25)*80.0)*40.0) * (pow(1.0-fract(time+0.5),8.0))*2.0 * (pow(1.0-fract((0.5+time)*0.33333)*0.5,6.0))*2.0;
    float noise2 = sin(sin(cos(fract(time))*(79.5+fract(time)))*(39.5+fract(time)));
    float noise3 = sin(sin(cos(fract(time))*(400.0))*(30.5+fract(time)));

    //float bassMix = 0.5 * pow((sin(3.1415*time*0.01)),101.0)+1.0;
    float bassMix = 0.5 * pow((sin(3.1415*time*0.5)),1.0)+0.5;
    float bass = sin(time*100.0+sin(3.1415*time*1.0)*2.0) + sin(time*200.0) * 0.15;
    bass *= 1.5;
    float bass2 = sin(time*150.6) + sin(time*300.0) * 0.15;
    bass2 *= 1.0;

    float resultBass = mix(bass,bass2,bassMix);

    return vec2(noise4*0.5 + resultBass   ,resultBass + noise4 * noise3 *0.5);
}
