// ORIGINAL https://www.shadertoy.com/view/XtscWS
const mat4 lightB2 = mat4(
    251,166,10,165,
    142,9,212,250,
    7,165,250,168,
    220,250,142,4
)/255.;

vec4 lightColorB2(vec2 fragCoord)
{        
    ivec2 gridPos = ivec2(fragCoord) % 4;         

    float factor = lightB2[gridPos.x][3 - gridPos.y];

    return vec4(vec3(factor), 1.);
}

// GREY LIGHT TONE ////
// diffs at [1][3], [3][1] with value of 216/255.
const mat4 lightB1 = mat4(
    251,166,10,165,
    142,9,212,216,
    7,165,250,168,
    220,216,142,4
)/255.;

vec4 lightColorB1(vec2 fragCoord)
{        
    ivec2 gridPos = ivec2(fragCoord) % 4;         

    float factor = lightB1[gridPos.x][3 - gridPos.y];

    return vec4(vec3(factor), 1.);
}

// DARK TONE
// diffs at [1][3], [3][1] with value of 8/255.
const mat4 darkB2 = mat4(
    251,166,10,165,
    142,9,212,8,
    7,165,250,168,
    220,8,142,4
)/255.;

vec4 darkColorB(vec2 fragCoord)
{        
    ivec2 gridPos = ivec2(fragCoord) % 4;         

    float factor = darkB2[gridPos.x][3 - gridPos.y];

    return vec4(vec3(factor), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{     
    vec2 uv = fragCoord.xy / iResolution.xy;

    if (uv.x <= 0.33)        
       	fragColor = lightColorB1(fragCoord);    
    else if (uv.x <= 0.66)        
       	fragColor = lightColorB2(fragCoord);
    else 
        fragColor = darkColorB(fragCoord);
}
