vec3 k = vec3(.4,-.215,.9);

vec3 m( vec3 p )
{
    p -= iTime;
	for( int i=0; i<25; i++ ) 
        p = reflect( abs(p.xzy)-9., k );
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 d = normalize( vec3(fragCoord,1.0)/iResolution), o = d;

    for( int i=0; i<49; i++ ) 
        o += d * m(o).x * .8;

    fragColor = texture( iChannel0, m(o).yz*.1 ) * (.2 + 99.*m(o-k*.01).x) ;
}
