void mainImage( out vec4 f, in vec2 w )
{
   float a=iDate.w,t=0.,b=cos(a),c=sin(a);

   for(int i=0;i<200;i++)
   {
      vec3 p=vec3(w,1.)/iResolution*t-1.;
       p.xz*=mat2(b,c,-c,b);
       for(int j=0;j<20;j++)
          p=reflect(p,p.yxz)+p*.1, p.y=-p.y;
       a=t;
       t+=min(length(p)-1.,1.)*.005;
   }

   f=vec4(2,3,4,1)/6./t+abs(a-t)*30.;
}
