// Title: "Hope", Author: Taras Palayda
// This shader is prepared for the first Wroclaw Shader Competition
// Organized by Faculty of Physics and Astronomy, Khronos Chapter 
// and SIggraph Chapter from Wroclaw

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = (-1.0+2.0*q)*vec2(iResolution.x/iResolution.y,1.0);

	float rho = sin(iChannelTime[0]/p.x*p.y)*(1.0-length(p)); 
	float ang = atan(p.x,p.y);
	float shear = rho+iTime; 
	float c = cos(shear), s=sin(shear);

	float r;
	r = rho; 
    float dens = exp(c*r);
	float phase = 10.0*(ang-shear);

    ang = sin(phase);
	q = r*vec2(cos(ang),sin(ang));

    float spires = 10.0*sin(phase);

    dens /= c+spires;	

	vec3 col = vec3(dens,dens*p.x*p.x,dens);

	fragColor = vec4(col,1.0);
}
