void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float t = mod(30.0*iDate.w,60.0)/60.0;
    vec3 col = texture(iChannel0, vec2(uv.x, -uv.y)).xyz;
    vec2 e = vec2(1.0/iResolution.x, 1.0/iResolution.y);
    vec3 colTop = texture(iChannel0, vec2(t*uv.y, (1.0-t) *(uv.y-e.y))).xyz;
	fragColor = vec4(clamp((col-colTop)*2.0,0.0,1.0),1.0);
}
