void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 100.0 + 2000.0 * fragCoord.xy / iResolution.xy;

float x = p.x;
float y = p.y;
float mov0 = x+y+cos(sin(iTime)*20000.0)*1000.+sin(x/2000.)*9000.;
float mov1 = y / 9.9 +  iTime;
float mov2 = x / 100.2;
float c1 = abs(sin(mov1+iTime)/2.+mov2/2.-mov1-mov2+iTime);
float c2 = abs(sin(c1+sin(mov0/1000.+iTime)+sin(y/40.+iTime)+sin((x+y)/200.)*9.));
float c3 = abs(sin(c2+cos(mov1+mov2+c2)+cos(mov2)+sin(x/10000.)));
fragColor = vec4(c1,c2,c3,1);

}

vec2 mainSound( in int samp,float time)
{
    return vec2( sin(6.2831*440.0*time)*exp(-3.0*time) );
}
