void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;
	vec2 position = 0.5 - uv;

    vec2 uva = vec2(position.x, position.y / aspect);
	//uva.x += .3 * (mouse.x / 2.0);
	//uva.y += .5 * (mouse.y / 2.0);

	float r = 10.0 * sqrt(dot(uva, uva));	
    vec2 uvd = uva;
	uvd.x += .1 * cos(10. * uvd.y + 0.5 * iTime );
	uvd.y += .1 * sin(10. * uvd.x + 0.5 * iTime );

	float r1 = 10.0 * sqrt(dot(uvd, uvd));
	float value = (1.0 * sin(20.0 * r1));

	float col = smoothstep(0.01, 0.5, value);
	vec3 color = vec3(col, col, col);
	fragColor = vec4(color, 1.0);
}
