 const vec3 circleColor = vec3(1., 0.5, 0.);
const float circleRad = 0.5;

highp float rand(vec2 co)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Setup screen coordinates.
vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv.x -= iResolution.x/iResolution.y/2.;
    uv.y -= 0.5;
    uv *= 2.;

    // Check if we set the pixel or not.
    if(length(uv) <= circleRad) {
		fragColor = vec4(circleColor * ((rand(vec2(iDate.z, iDate.w)) * 0.6) - length(uv)), 1.);
    } else {
        fragColor = vec4(0.);
    }
}
