float hash( float p )
{
    vec2 p2 = fract(vec2(p) * 0.2345);
	p2 += dot(p2.yx, p2.xy+19.19);
	return fract(p2.x * p2.y);
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0;
    float res = mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                    mix( hash(n- 0.01), hash(n- 0.10),f.x),f.y);
    return res;
}

float wisp(in vec2 p) 
{
    p *= .25;
    float f;

	f = 0.5000 * noise(p); p = p * 3.02; //p.y -= gTime*.2;
	f += 0.2500 * noise(p); p = p * 3.03; //p.y += gTime*.06;
	f += 0.1250 * noise(p); p = p * 3.01;
	f += 0.0625   * noise(p); p =  p * 3.03;
	f += 0.03125  * noise(p); p =  p * 3.02;
	f += 0.015625 * noise(p);
    return f;
}

void drawCloud(vec2 pos, inout vec3 col) 
{
    float cloudIntensity = wisp(pos);
    col.b = 1.0;
    col.r+=cloudIntensity;
    col.g += cloudIntensity;
}

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 5.0 - 2.50;
    float x = uv.x; float y = uv.y;
	float card = pow(y - pow(abs(x),0.5),2.0) + pow(x,2.0);
    card = min(1.0/pow(card,2.0),1.0);
    fragColour = vec4(card/2.0,card/2.0,1.0,0.0); 
    drawCloud(uv+555.0+iTime, fragColour.rgb);
}
