//Complex utility functions from https://harrisonclarke.wordpress.com/2010/02/07/fractals-on-the-gpu-glsl/ 
//and http://www.codeproject.com/Articles/492355/Domain-Coloring-Method-on-GPU 

float c_abs(vec2 z)
{
    return sqrt(z.x * z.x + z.y * z.y);
}

float c_arg(vec2 z)
{
    return atan(z.y, z.x);
}

vec2 c_mult(vec2 a, vec2 b) 
{
    return vec2(a.x * b.x - a.y * b.y, (a.x+a.y)*(b.x+b.y) - a.x*b.x - a.y*b.y);
}

vec2 c_div(vec2 a, vec2 b)
{
    return vec2(((a.x*b.x+a.y*b.y)/(b.x*b.x+b.y*b.y)),((a.y*b.x-a.x*b.y)/(b.x*b.x+b.y*b.y)));
}

vec2 c_pow(vec2 z)
{
    vec2 y = z;
    for (int i=1; i<50; i++){
    	y = c_mult(y, z);
    }
    return y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.xy);
    vec2 uv = xy * iTime * 0.1;
    vec2 a = c_pow(uv) - vec2(1.0, 0.0);
    vec2 b = uv - vec2(2.0, 0.0) - vec2(0.0, 1.0);
    vec2 c = c_mult(uv, uv) + vec2(2.0, 0.0) + vec2(0.0, iTime);
    vec2 top = c_mult(a, c_pow(b));
    vec2 result = c_div(top, c);
    fragColor = vec4(vec3(c_arg(result), mod(c_arg(result), 3.0) / 2.0, mod(c_arg(result), 5.0)) / 4.0, 1.0);
}
