vec3 hsl2rgb(in float h, float s, float l) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0);
  return l + s * (rgb-0.5)*(1.0-abs(2.0*l-1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float t = 10000.0 + iTime;
  float w = iResolution.x, h = iResolution.y;
  float x = fragCoord.x, y = fragCoord.y;
  float mwh = min(w, h);
  float xr = x/mwh, yr = y/mwh;
  float hue = sin(
      (1.0+10.0*sin(t/5.0+t/2000.0*(xr+yr)/5.5+2.0))
    ) * 1.0+sin(t/5.0+xr*4.0)
    + sin(sin(xr*2.5+yr) + sin(t/4.0))/10.0
    + sin(xr*t/800.0/1.5) + mod(yr*t/800.0,2.0)-0.1
  ;
  float sat = hue*sin(sin(xr/10.0) + sin(yr/10.0));
  float lum = mod(hue,1.0)*mod(sat,1.0)*2.0;
  fragColor = vec4(hsl2rgb(hue,sat,lum), 1.0);
}
