void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.y/iResolution.x;
    vec2 P = 75.0 * (fragCoord.xy/iResolution.x - vec2(0.5,ratio/2.0));
    float C = 1.2 * sin((atan(P.x,P.y) + length(P) + mod(10.0*iTime,6.2831855)) * 5.0);
    fragColor = vec4(C, C, C, 1.0);
}
