vec2 rotate(vec2 p, float theta) {
    float s = sin(theta);
    float c = cos(theta);
    return vec2(p.x * c - p.y * s, p.x * s + p.y * c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float zoomOutFactor = 50.0f;
    vec2 xy = zoomOutFactor * ( 2.0f * uv - 1.0f);
    xy.y *= iResolution.y / iResolution.x;
    float r = length(xy);
	float fft  = texelFetch( iChannel0, ivec2(r/zoomOutFactor,0), 0 ).x;
    xy = rotate(xy, fft * 25.0);
    float t = r * (fft * 2.0);
    float dist = length(xy - vec2(t * cos(t), t * sin(t)));
	fragColor = vec4(abs(dist)/10.0);
}
