float r = 100.0;
float halo = 50.0;
float cursorSize = 50.0;
float lineWidth = .15;
float lineOffset = 150.0;
float bigR = 250.0;

bool isDrawing(float d, float dxy) {
    return dxy > bigR - lineWidth * d && dxy < bigR + lineWidth * sin(iTime) * d;
}

vec4 lineColor(float lineIntensity) {
    return vec4(lineIntensity * .25, lineIntensity, lineIntensity, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mid = vec2(iResolution[0] * .5, iResolution[1] * .5);
    float d = distance(fragCoord, mid);
    float lineIntensity = 1.0 - sin((d - r) * .006);

    float d00 = distance(fragCoord, vec2(mid[0] - lineOffset, mid[1] - lineOffset));
    float d01 = distance(fragCoord, vec2(mid[0] - lineOffset, mid[1] + lineOffset));
    float d10 = distance(fragCoord, vec2(mid[0] + lineOffset, mid[1] - lineOffset));
    float d11 = distance(fragCoord, vec2(mid[0] + lineOffset, mid[1] + lineOffset));

    if (d > r && fragCoord[0] < mid[0] && isDrawing(d, d00)) {
        fragColor = lineColor(lineIntensity);
    }
    else if (d > r && fragCoord[1] > mid[1] && isDrawing(d, d01)) {
        fragColor = lineColor(lineIntensity);
    }
    else if (d > r && fragCoord[1] < mid[1] && isDrawing(d, d10)) {
        fragColor = lineColor(lineIntensity);
    }
    else if (d > r && fragCoord[0] > mid[0] && isDrawing(d, d11)) {
        fragColor = lineColor(lineIntensity);
    }
    else if (d < r) {
        if (d < r * .75) {
            fragColor = vec4(.75, 1, 1, 0);
        }
        else {
            float intensity = cos((d - r * .75) * .05);
        	fragColor = vec4(.75 * intensity, 1, 1, 0);
        }
    }
    else if (d < r + halo) {
        float intensity = 1.0 - (distance(fragCoord, mid) - r) / halo;

        intensity *= abs(sin(iTime));
        fragColor = vec4(intensity * .25, intensity, intensity, 0);
    }
    else {
        fragColor = vec4(0, 0, 0, 0);
    }
}
