void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

 	uv.x *= iResolution.x/iResolution.y;   
       uv -= vec2(0.5, .5);
    uv += iMouse.xy / iResolution.xy;

    float mult =28.0;
    float dx = round((uv.x-.5) * mult);
    float dy = round((uv.y-.5) * mult);


    float a = sqrt(dx*dx + dy*dy)/mult * 2.0 * 3.1415-iTime/18.0;

    mult *= 17.0/1000.0;
    a*=20.0;
    uv.x *= sin(-a)/mult+10.0;
    uv.y *= cos(-a)/mult+10.0;
    uv.x *= (sin(a) + 1.0)/2.0 * .12 + 1.0;
    uv.y *= (sin(a) + 1.0) / 2.0 * .12 + 1.0;

	fragColor = texture(iChannel0, uv);
}
