void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 pixelSize = vec2(1.0, 1.0) / vec2(70, 50); //pixelAmount by x and y
	vec2 pixelId = floor(uv / pixelSize);

    vec2 leftBot = pixelId * pixelSize;
   	vec2 rightBot = vec2((pixelId.x + 1.0) * pixelSize.x, pixelId.y * pixelSize.y);
    vec2 leftTop = vec2(pixelId.x * pixelSize.x, (pixelId.y + 1.0) * pixelSize.y);
    vec2 rightTop = (pixelId + 1.0) * pixelSize;
    vec2 center = (pixelId + 0.5) * pixelSize;

    fragColor = texture(iChannel0, leftBot) * 0.2 + texture(iChannel0, rightBot) * 0.2 
        + texture(iChannel0, leftTop) * 0.2 + texture(iChannel0, rightTop) * 0.2 + texture(iChannel0, center) * 0.2; 

}
