float plot(float a, float b, float k) {
	return smoothstep(3.0 / iResolution.y * sqrt(1.0 + k * k), 0.0, abs(a - b));
}

float fsin(float x) {
	return sin(x * 4.0) * 0.5;
}

float dfsin(float x) {
	return cos(x * 4.0) * 2.0;
}

float fexp(float x) {
	return exp(-x) * 0.3 - 0.5;
}

float dfexp(float x) {
	return exp(-x) * -0.3;
}

float smin(float x, float k) {
    float a = fsin(x);
    float b = fexp(x);
	float h = clamp(0.5 + 0.5 * (a - b) / k, 0.0, 1.0);
    return mix(a, b, h) - k * h * (1.0 - h);
}

float dsmin(float x, float k) {
	return (smin(x + 0.001, k) - smin(x, k)) / 0.001;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float k = 0.1 * (cos(iTime) + 1.0) + 0.01;
    vec3 bg = mix(vec3(1.0), vec3(1.0, 0.0, 0.0), plot(uv.y, fsin(uv.x), dfsin(uv.x)));
    bg = mix(bg, vec3(0.0, 1.0, 0.0), plot(uv.y, fexp(uv.x), dfexp(uv.x)));
    bg = mix(bg, vec3(0.0, 0.0, 1.0), plot(uv.y, smin(uv.x, k), dsmin(uv.x, k)));
	fragColor = vec4(bg,1.0);
}
