void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv*2.0 - 1.0; // Map coordinate to -1, 1
    uv.x *= iResolution.x/iResolution.y; // Stretch to aspect ration 

    vec3 val = vec3(uv.x, uv.y, uv.x*uv.y) * length(abs(uv) - 0.7) * (sin(iTime/1.0)*3.0+4.0);
    fragColor = vec4(fract(val*5.0), 1.0);
}
