vec2 diskCenter = vec2(0,0);
const float diskRadius =1.0;

vec2 transform_to_apply(vec2 fragCoord){
	vec2 pt;
    pt = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y; 
    return pt;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
   // vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);

    //Apply change of coordinates from prixel
    vec2 pt = transform_to_apply(fragCoord.xy);

   // vec2 diskCenter = vec2(cos(iTime), 0);

    //Determine if point is inside the disk
    if (distance(pt.xy, diskCenter) < diskRadius)
        fragColor = vec4(1,0,0,1.0);
    else
        fragColor = vec4(0,0,0,1.0);
}
