float plotline(vec2 uv, float line)
{
    float pixelsize = 6.0/iResolution.y;

    return	smoothstep( line-pixelsize, line, uv.y ) - 
        	smoothstep( line, line+pixelsize, uv.y );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float uvx = uv.x;


    vec4 bg = vec4(0.6,0.6,0.6,1.0);

    if( uv.x < 0.8)
    {
        uv.x = uv.x*6.0;

        uv.y = uv.y*4.0;

    	float lineA = 2.0+sin(4.0*sin(2.0*iTime+uv.x));

    	float lineB = 2.0+sin(2.0*iTime+uv.x);

    	lineA = plotline(uv,lineA) * uvx*2.0;

    	lineB = plotline(uv,lineB) * uvx*2.0;

    	bg = mix(bg,vec4(1.0,1.0,1.0,1.0),lineA);

    	bg = mix(bg,vec4(0.2,0.2,0.2,1.0),lineB);

    }

    fragColor = vec4(bg);
}
