void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 3.0;// + iTime / 3.;

	vec2 uv = zoom * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    // Normalized pixel coordinates (from 0 to 1)
   // vec2 uv = fragCoord/iResolution.xy;

    float r = sqrt(uv.x*uv.x + uv.y*uv.y);
    float theta = atan(uv.x, -uv.y);

    vec2 check = vec2(r, theta);

    vec2 size = vec2(iTime,iTime) / 3.0;
        float total = floor(check.x*float(size.x)) +
                  floor(check.y*float(size.y));
    bool isEven = mod(total,2.0)==0.0;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float c = isEven ? 1.0 : 0.;

    vec3 rgb = vec3(c);

    // Output to screen
    fragColor = vec4(rgb,1.0);
}
