void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cc = uv - vec2(0.5,0.5);
    vec2 po = vec2( atan(cc.y,cc.x) , length(cc) );

    //float i=uv.x;
    float fft=texture(iChannel2,vec2(0.5,0.5)).r;

    float s=32.0;
    float n=8.0+ceil(fft);
    float z=.1+fft;

	float f=(2.0*PI/n);

    float m=mod((1.0*PI)+po.x,f);

    if(m>f/1.0){ m=f-m; }

    po.x = m+(iTime*s);
    po.y /=z;

    cc= vec2(sin(po.x),cos(po.x))*po.y;


    // Output to screen
    fragColor = texture( iChannel1, cc+vec2(0.5,0.5) ).rgba;

    fragColor=fragColor*2.0-1.0; // contrast

}
