float sine(vec2 pos, float phase){
    return abs( 2.584/(sin(2.0*3.14*pos.x  +  phase) * 0.5 + 0.5 - pos.y * 2.0 + .5));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 st = gl_FragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

	float r = sine(st, -0.828) *
        sine(st, fract(iTime * 1.25) *6.28) +
        sine(st, fract(iTime * 0.242) *6.28) +
        sine(st, fract(iTime * 1.858) *6.28) +
        sine(st, fract(iTime * -0.239) * 6.28) ;

    float g =  smoothstep(0.20, 0.5, sine(st, fract(iTime * 1.617) * 6.28) );

	float b = sine(st, -0.28) *
        sine(st, fract(iTime * 1.322) *6.28) -
        sine(st, fract(iTime * -0.398) *6.28) -
        sine(st, fract(iTime * -1.199) * 6.28) ;

    fragColor = vec4(max(r, g) *0.002, sin(min(r, b) *0.007) + max(g, b) * 0.044, b * 0.032,1.0);
}
