void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 c = texture(iChannel0, uv);
    c.rgb = sin(c.rgb*(6.2+iMouse.y/iResolution.y*20.)+iTime+vec3(3., 1.5,.5*texture(iChannel1, vec2(1.5*length(uv-.5),1.)).r))*.5+.5;
    fragColor = c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float z = (texture(iChannel2, vec2(length(uv-.5),1.)).r)*(iMouse.x/iResolution.x*.2+.02);
    float zoom = (1.-z);///sin(iTime*20.)*.1+.9;
    uv-=.5;
    uv*=zoom;
    uv+=.5;
    vec4 o = texture(iChannel0, uv);
    vec4 c = texture(iChannel3,uv);
    c = mix(c,o,.95);
    fragColor = c;
}
