void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec3 sceneColor = texture( iChannel0, uv ).rgb;
    vec3 noiseColor = texture( iChannel1, uv * 1.0 ).rgb; //scaled tex 2x
    vec3 backColor = texture( iChannel2, uv ).rgb;
    fragColor.a = 1.0;

    float yband = 0.2;
    float ymin = 0.4 + 0.6*sin(iTime * 0.3); //anchored to [-0.2 - 1.0] * 0.7
    float ymax = ymin + yband;

    // uv.y < ymin || alpha 'fail' case
    fragColor.rgb = backColor;

    if ( uv.y > ymax )
    {
        fragColor.rgb = sceneColor;
    }
    else if ( noiseColor.r < ((uv.y - ymin) / yband) )
    {
        fragColor.rgb = sceneColor;
    }


}
