void mainImage( out vec4 color, in vec2 pixCoords )
{
    color = vec4 (1.0, 1.0, 1.0, 1.0);

    vec2 newCoords = (pixCoords - iResolution.xy/2.0) * 10.0;
    vec2 center;
    center.x += (1.0 - cos(iTime)/2.0);
    center.y += (1.0 - sin(iTime)/2.0);

	float radius = 80.0;

    float distance = length(pixCoords - newCoords);

    radius += cos(radius) + 30.0 * cos(iTime);

    if (mod(distance, 200.0) < radius)
    {
        color = vec4 (1.0, 0, 0, 1.0);
    }
}
