void mainImage (out vec4 fragColor, in vec2 fragCoord) {
  //1. scale coordinates based on resolution
  vec2 xy = fragCoord.xy / iResolution.xy;  

  //2. scale - this could be a uniform
  float scale = sin(iTime * 0.1) * 10.0;
  xy *= scale;

  //3. sample texture using scaled coordinates
  fragColor = texture(iChannel0, xy);
}
