void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // distance float
    float d = distance(vec2(iResolution.x / 2., iResolution.y / 1.2), fragCoord.xy) * (1./iResolution.x+0.11)*0.01;

    // colors
    vec3 white = vec3(1., 1., 1.);
    vec3 eightWhite = vec3(0.8, 0.8, 0.8);
    vec3 pink = vec3(0.41, 0.41, 0.91);
    vec3 blue = vec3(0.94, 0.36, 0.60);

    vec3 color = mix(
        mix(white, eightWhite, uv.x),
        mix(pink, blue, uv.y),
       	d
    );	


    fragColor = vec4(color, 1.);
}
