// Tessellated Mandelbrot
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

void mainImage(out vec4 f, in vec2 p)
{
	// based on IQ's https://www.shadertoy.com/view/lllGWH
    vec2 c = vec2(-.74441,.18631) + 3.*(p.xy/iResolution.y-.5)*pow(.01,1.-cos(.2*iTime));
    vec2 z = vec2(0.);
    for (int i=0; i<60; i++) {
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c;
        if (dot(z,z)>1e4) {
            // texture coordinates
            float tu = mod(atan(z.y, z.x)/6.283185+iTime*.5, 1.); 
            float tv = log2(log(dot(z,z))/log(1e4)); 
            f = texture(iChannel0, vec2(tu,tv)).rgga*1.4;
            return;
        }
    }
    // ghosty britney
	f = texture(iChannel0, z+.5).brra*1.3-0.25;
}
