float hash(float n) {
    return fract( sin(n*223.4023)*245.34685 )*16.0;
}
float grad(float hash, float x, float y, float z) {
    if (hash<=1.0) {
        return x+y;
    }
    else if(hash<=2.0) {
        return -x+y;
    }
    else if(hash<=3.0) {
        return x-y;
    }
    else if(hash<=4.0) {
        return -x-y;
    }
    else if(hash<=5.0) {
        return x+z;
    }
    else if(hash<=6.0) {
        return -x+z;
    }
    else if(hash<=7.0) {
        return x-z;
    }
    else if(hash<=8.0) {
        return -x-z;
    }
    else if(hash<=9.0) {
        return y+z;
    }
    else if(hash<=10.0) {
        return -y+z;
    }
    else if(hash<=11.0) {
        return y-z;
    }
    else if(hash<=12.0) {
        return -y-z;
    }
    else if(hash<=13.0) {
        return y+x;
    }
    else if(hash<=14.0) {
        return -y+z;
    }
    else if(hash<=15.0) {
        return y-x;
    }
    else {
        return -y-z;
    }
}
float perlin(vec3 x) {

    vec3 p = floor(x);
    vec3 f = fract(x);

    float n = p.x + p.y*57.0 + p.z*113.0;

    float aaa = grad(hash( n ),f.x,f.y,f.z);
    float baa = grad(hash( n+1.0 ),f.x-1.0,f.y,f.z);
    float aba = grad(hash( n+57.0 ),f.x,f.y-1.0,f.z);
    float bba = grad(hash( n+58.0 ),f.x-1.0,f.y-1.0,f.z);

    float aab = grad(hash( n+113.0 ),f.x,f.y,f.z-1.0);
    float bab = grad(hash( n+114.0 ),f.x-1.0,f.y,f.z-1.0);
    float abb = grad(hash( n+170.0 ),f.x,f.y-1.0,f.z-1.0);
    float bbb = grad(hash( n+171.0 ),f.x-1.0,f.y-1.0,f.z-1.0);

    f = f*f*(3.0-2.0*f);
    float res = mix( mix( mix(aaa,baa,f.x), mix(aba,bba,f.x), f.y ),
                     mix( mix(aab,bab,f.x), mix(abb,bbb,f.x), f.y ),
                    f.z );

    return res;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/ iResolution.yy;
    float pval = 0.0;
    pval += perlin(vec3(uv,iTime));
    pval += perlin(2.0*vec3(uv,iTime))/2.0;
    pval += perlin(4.0*vec3(uv,iTime))/4.0;
    pval += perlin(8.0*vec3(uv,iTime))/8.0;
    pval += perlin(16.0*vec3(uv,iTime))/16.0;
    pval += perlin(32.0*vec3(uv,iTime))/32.0;
    pval += perlin(64.0*vec3(uv,iTime))/64.0;
    fragColor = vec4(vec3(0.5+0.5*pval),1.0);
}
