// Author : FabriceNeyret2
// https://www.shadertoy.com/user/FabriceNeyret2

#define PI 3.14159265358979323846

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    fragColor.xy = fragCoord * 6./iResolution.y;
    //fragColor.zw =mat2(-1,1,1,1)*fragColor.xy;

    fragColor.zw =mat2(-.5,.5,.5,.5)*fragColor.xy;

    fragColor = 9.*cos(PI * fragColor);
    fragColor += fragColor.x*fragColor.y*fragColor.z*fragColor.w -fragColor;
}
