vec4 appleOfMyEye(sampler2D sam, in vec2 uv){
    float i = iTime * 0.1;
    i = i * i;
    vec4 col = texture(sam, uv + vec2(-i, i));

	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
	uv.x *= iResolution.x / iResolution.y;

    float r = length(uv) * 1.0;
    uv.y /= atan(uv.y,r);
    uv.x /= atan(uv.x,r);

    float y = uv.y;
    uv.y = fract(uv.x);
    uv.x = fract(y);

    vec4 col  = appleOfMyEye(iChannel0, uv);
	vec4 col2 = appleOfMyEye(iChannel1, uv);

    fragColor = mix(col,col2,1.5);
}
