//This function was borrowed from
//https://www.shadertoy.com/view/ll2GD3
//Palettes by iq
#define p(t, a, b, c, d) a+b*cos(6.28318*(c*t+d))

void mainImage( out vec4 o, in vec2 q )
{
   vec2 u = q / iResolution.xy;
   float s = q.x/u.x/2.; //spacing

   //uncomment to control spacing with iMouse.y
   //s /= floor(iMouse.y*iResolution.z);

   //palettes
   vec3 c1 = p(u.x,
               vec3(0.5,0.5,0.5),
               vec3(0.5,0.5,0.5),
               vec3(1.0,1.0,1.0),
               vec3(0.0,0.33,0.67));
   vec3 c2 = p(u.x-iTime*.1,
               vec3(0.5,0.5,0.5),
               vec3(0.5,0.5,0.5),
               vec3(1.0,1.0,1.0),
               vec3(0.0,0.10,0.20));

   o = vec4(mix(c1*float(u.y>1./3.),
                c2*float(u.y<2./3.),
                float(sin(u.x*s*6.28318)>0.)
               ),1.);
}
