float houseOnOff = 1.0; // On/off for house layer
float chimneyOnOff = 1.0; // On/off for chimney layer
float windowsOnOff = 1.0; // On/off for windows layer

vec2 hPos = vec2(0.25,0.0);
float hWidth = 0.5;
float hHeight = 0.4;
vec3 hCol = vec3(0.5,0.4,0.3);
vec2 cPos = vec2(0.35,0.71);
float cWidth = 0.075;
float cHeight = 0.1;
vec3 cCol = vec3(0.5,0.25,0.25);
vec2 w1Pos = vec2(0.45, 0.25);
vec2 w2Pos = vec2(0.2, 0.25);
vec2 w3Pos = vec2(0.2, 0.025);
float wSize = 0.12;
vec3 wCol = vec3(0.75,0.75,0.5);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatio = iResolution.y/iResolution.x;
    uv.y *= aspectRatio;
	vec4 Layer1 = vec4((1.0-uv.y)*0.5, (1.0-uv.y), 1.0, 1.0);
	vec4 Layer2 = vec4(0.0,0.0,0.0,0.0);
	vec4 Layer3 = vec4(0.0,0.0,0.0,0.0);
	vec4 Layer4 = vec4(0.0,0.0,0.0,0.0);
    hPos *=aspectRatio;
    cPos *=aspectRatio;
    Layer2 = vec4(hCol, 1.0);
    Layer3 = vec4(cCol, 1.0);
    Layer4 = vec4(wCol, 1.0);
    float hInterpX = step(hPos.x,uv.x) - step(hPos.x + hWidth, uv.x);
    float hInterpY = step(hPos.y,uv.y) - step(hPos.y + hHeight, uv.y);
    float cInterpX = step(cPos.x,uv.x) - step(cPos.x + cWidth, uv.x);
    float cInterpY = step(cPos.y,uv.y) - step(cPos.y + cHeight, uv.y);
    float w1InterpX = step(w1Pos.x,uv.x) - step(w1Pos.x + wSize, uv.x);
    float w1InterpY = step(w1Pos.y,uv.y) - step(w1Pos.y + wSize, uv.y);
    float w2InterpX = step(w2Pos.x,uv.x) - step(w2Pos.x + wSize, uv.x);
    float w2InterpY = step(w2Pos.y,uv.y) - step(w2Pos.y + wSize, uv.y);
    float w3InterpX = step(w3Pos.x,uv.x) - step(w3Pos.x + wSize, uv.x);
    float w3InterpY = step(w3Pos.y,uv.y) - step(w3Pos.y + wSize, uv.y);
    Layer2.a *= (hInterpX*hInterpY);
    Layer3.a *= (cInterpX*cInterpY);
    Layer4.a *= ((w1InterpX*w1InterpY) + (w2InterpX*w2InterpY) + (w3InterpX*w3InterpY));

    Layer2.a *= houseOnOff;
    Layer3.a *= chimneyOnOff;
    Layer4.a *= windowsOnOff;

    vec4 combo = mix(Layer1, Layer2, Layer2.a);
    combo = mix(combo, Layer3, Layer3.a);
    combo = mix(combo, Layer4, Layer4.a);
    fragColor = combo;

}
