void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*32.0;

    // Time varying pixel color
    vec3 a = 0.5 + 1.0*sin(iTime+uv.yyy+vec3(0,2,4));
    a += sin(iTime+uv.xyy+vec3(0,2,4));
    a += cos(iTime+uv.yxy+vec3(0,1,4));

    // Output to screen
    fragColor = vec4(a,1.0);
}
