// simple random grid...

#define rand(p)  fract(sin(dot(p ,vec2(12.9898,78.233))) * 43758.5453)

float grid(vec2 uv, float scale, float thickness)
{
    //vec2 g2 = step(thickness,fract(uv));
    vec2 g2 = smoothstep(0.,1.5*scale/iResolution.y,abs(fract(uv+.5)-.5)-thickness/2.);
    return min(g2.x,g2.y);
}


float RandomGrid(vec2 uv,float scale)
{
    uv *= scale;
    float g = grid(uv,scale,0.25);
	return rand(floor(uv)/scale)*g;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = ( fragCoord -.5*iResolution.xy ) / iResolution.y;
    uv.xy += iTime*0.25;
    float scale = 20.0;
    float v = RandomGrid(uv,scale);
    vec3 c1 = vec3(0.2,0.2,0.2);
    vec3 c2 = vec3(0.0,0.9,0.9);
    fragColor = vec4(mix(c1,c2,v),1.0);
}
